PrintWriter writer;
try {   
    writer = new PrintWriter(
            new FileWriter(LOG_FILE_DIR + LOG_FILE, true)
        );

    while(e != null){
        e.printStackTrace(writer);
        e = e.getCause();
    }

    writer.println();
    writer.println("--------------------------------------------------");
    writer.flush();
    writer.close();
} catch (IOException ex) {
    ex.printStackTrace();
}

