public void fetchMultiData() throws RuntimeException {

    int serviceCountId = serviceCount;

    if (executerPool == null)
        setMultiProcesseorSize(MAX_POOL_SIZE);

    for (int i = 0; i &lt; serviceCountId; i++) {
        executerPool.execute(new ServiceRunnable(i) {
            public void run() {
                try {
                    serviceable[getCount()].fetchData();
                    synchronized (notifyObject) {
                        if ((--serviceCount) == 0) {
                            notifyObject.notify();
                        }
                    }
                } catch (RuntimeException e) {
                    synchronized (notifyObject) {
                        exception = e;
                        notifyObject.notify();// NOSONAR
                    }
                }
            }
        });
    }

    synchronized (notifyObject) {
        try {
            while (serviceCount != 0 &amp;&amp; exception == null) {
                notifyObject.wait();
            }
            if (exception != null) {
                throw exception;
            }
        } catch (InterruptedException e) {
            throw new RuntimeException(/*e.getMessage()*/);
        }
    }
    executerPool.shutdown();
    executerPool = null;
}

public void setMultiProcesseorSize(int size) {
    executerPool = new ThreadPoolExecutor(size, size, 0L, TimeUnit.MILLISECONDS, new LinkedBlockingQueue&lt;Runnable&gt;());
