package labs.lab3; import java.util.Stack; import java.util.EmptyStackException; import java.util.StringTokenizer; public class PostfixEvaluator { private Stack<Integer> stack; private String expression; private String token; public PostfixEvaluator(String e) { stack = new Stack<Integer>(); expression = e; } // Evaluate the postfix expression and return the evaluation result public int Evaluate() { int op1,op2; int result; StringTokenizer st = new StringTokenizer(expression);//split the expression into tokens String token=st.nextToken(); while (st.hasMoreTokens()){ if (Character.isDigit(token.charAt(0))) { int value = Integer.parseInt(token); stack.push(value); } else if (!Character.isDigit(token.charAt(0))) { op1=stack.pop(); op2=stack.pop(); result = Calculate(op1,op2,token.charAt(0)); stack.push(result); } } int answer = stack.pop(); return answer; } // Perform an operation on the two operands public int Calculate(int operand1, int operand2, char operation) { int result = 0; switch (operation) { case '+': result = operand1 + operand2; break; case '-': result = operand1 - operand2; break; case '/': result = operand1 / operand2; break; case '*': result = operand1 * operand2; break; case '%': result = operand1 % operand2; break; } return result; } } 
