 public class RefreshLoaderView extends SurfaceView implements SurfaceHolder.Callback { private LoaderThread mLoaderThread; private volatile SurfaceHolder mHolder; private static final int ANIMATION_TIME = 600; private final ExecutorService mExecutor; private Future mExecutingTask; public RefreshLoaderView(Context context) { super(context); ... init(); } private void init() { mLoaderThread = new LoaderThread(); ... } @Override public void surfaceChanged(SurfaceHolder holder, int arg1, int arg2, int arg3) { ... mHolder = this.getHolder(); } @Override public void surfaceCreated(SurfaceHolder holder) { //uninteresting stuff here } @Override public void surfaceDestroyed(SurfaceHolder holder) { stopThread(); } private void stopThread() { mLoaderThread.setRunning(false); if (mExecutingTask != null) { mExecutingTask.cancel(true); } } private void startThread() { if (mLoaderThread == null) { mLoaderThread = new LoaderThread(); } mLoaderThread.setRunning(true); mExecutingTask = mExecutor.submit(mLoaderThread); } public void notifyTaskStarted() { stopThread(); startThread(); mLoaderThread.setAction(LoaderThread.ANIMATION_TASK_STARTED); } public void notifyTaskFailed() { mLoaderThread.setAction(LoaderThread.ANIMATION_TASK_FAILED); } public void notifyTaskSuccess() { mLoaderThread.setAction(LoaderThread.ANIMATION_TASK_SUCCESS); } private class LoaderThread implements Runnable { private volatile boolean mRunning = false; private int mAction; private long mStartTime; private int mMode; public final static int ANIMATION_TASK_STARTED = 0; public final static int ANIMATION_TASK_FAILED = 1; public final static int ANIMATION_TASK_SUCCESS = 2; private final static int MODE_COMPLETING = 0; private final static int MODE_ENDING = 1; public LoaderThread() { mMode = 0; } public synchronized boolean isRunning() { return mRunning; } public synchronized void setRunning(boolean running) { mRunning = running; if (running) { mStartTime = System.currentTimeMillis(); } } public void setAction(int action) { mAction = action; } @Override public void run() { if (!mRunning) { return; } while (mRunning) { Canvas c = null; try { c = mHolder.lockCanvas(); synchronized (mHolder) { //switcho quello che devo animare if (c != null) { switch (mAction) { case ANIMATION_TASK_STARTED: animationTaskStarted(c); break; case ANIMATION_TASK_FAILED: animationTaskFailed(c, mMode); break; case ANIMATION_TASK_SUCCESS: animationTaskSuccess(c, mMode); break; } } } } catch (Exception e) { e.printStackTrace(); } finally { if (c != null) { mHolder.unlockCanvasAndPost(c); } } } } private void animationTaskStarted(Canvas canvas) { //do an animation here } private void animationCloseLoaderCycle(Canvas canvas) { //do stuff here ... } else { mStartTime = System.currentTimeMillis(); mMode = MODE_ENDING; } } private void queryThreadClose() { mProgress = 0; mMode = MODE_COMPLETING; mRunning = false; } private void animationTaskFailed(Canvas canvas, int mode) { switch (mode) { case MODE_COMPLETING: animationCloseLoaderCycle(canvas); break; case MODE_ENDING: if (System.currentTimeMillis() - mStartTime < ANIMATION_TIME) { //notify user task is failed } else { queryThreadClose(); } break; } } private void animationTaskSuccess(Canvas canvas, int mode) { switch (mode) { case MODE_COMPLETING: animationCloseLoaderCycle(canvas); break; case MODE_ENDING: if (System.currentTimeMillis() - mStartTime < ANIMATION_TIME) { //notify user task is failed } else { queryThreadClose(); } break; } } } public void onPause() { stopThread(); } public void onStop() { stopThread(); } public void onDestroy() { stopThread(); } } 
