public class Prefs extends PreferenceActivity implements OnSharedPreferenceChangeListener { @Override protected void onCreate(Bundle savedInstanceState) { // TODO Auto-generated method stub super.onCreate(savedInstanceState); addPreferencesFromResource(R.xml.prefs); PreferenceManager.setDefaultValues(Prefs.this, R.xml.prefs, false); initSummary(getPreferenceScreen()); } @Override protected void onResume() { super.onResume(); // Set up a listener whenever a key changes getPreferenceScreen().getSharedPreferences().registerOnSharedPreferenceChangeListener(this); } @Override protected void onPause() { super.onPause(); // Unregister the listener whenever a key changes getPreferenceScreen().getSharedPreferences().unregisterOnSharedPreferenceChangeListener(this); } public void onSharedPreferenceChanged(SharedPreferences sharedPreferences, String key) { updatePrefSummary(findPreference(key)); } private void initSummary(Preference p) { if (p instanceof PreferenceGroup) { PreferenceGroup pGrp = (PreferenceGroup) p; for (int i = 0; i < pGrp.getPreferenceCount(); i++) { initSummary(pGrp.getPreference(i)); } } else { updatePrefSummary(p); } } private void updatePrefSummary(Preference p) { if (p instanceof ListPreference) { ListPreference listPref = (ListPreference) p; System.out.println("---------- listPref -------------"+ listPref.getEntry()); if(listPref.getEntry() != null) p.setSummary(listPref.getEntry()); } if (p instanceof EditTextPreference) { EditTextPreference editTextPref = (EditTextPreference) p; if(editTextPref.getText() != null){ if (p.getTitle().toString().contains("assword") || p.getTitle().toString().contains("in")) { p.setSummary("******"); } else { p.setSummary(editTextPref.getText()); } } else p.setSummary(editTextPref.getSummary()); } if (p instanceof MultiSelectListPreference) { EditTextPreference editTextPref = (EditTextPreference) p; if(editTextPref.getText()!= null) p.setSummary(editTextPref.getText()); } } 
