package com.example.movingbackground; import org.andengine.entity.scene.Scene; import org.andengine.entity.scene.background.AutoParallaxBackground; import org.andengine.entity.scene.background.ParallaxBackground.ParallaxEntity; import org.andengine.entity.sprite.Sprite; import org.andengine.opengl.texture.TextureOptions; import org.andengine.opengl.texture.atlas.bitmap.BitmapTextureAtlas; import org.andengine.opengl.texture.atlas.bitmap.BitmapTextureAtlasTextureRegionFactory; import org.andengine.opengl.texture.region.TextureRegion; public class GamePlay extends Scene { private MainActivity mCxt = null; private Sprite background_1 = null; private Sprite background_2 = null; private Sprite background_3 = null; private AutoParallaxBackground bg = null; private Sprite player = null; public GamePlay(MainActivity mCxt) { this.mCxt = mCxt; bg = new AutoParallaxBackground(0.0f, 0.0f, 0.0f, 1f); background_1 = new Sprite(0, 0, getSpriteRegion(640, 1136, "bg.png"), this.mCxt.getVertexBufferObjectManager()); background_2 = new Sprite(0, 0, getSpriteRegion(640, 1136, "bg.png"), this.mCxt.getVertexBufferObjectManager()); background_3 = new Sprite(0, 0, getSpriteRegion(640, 1136, "bg.png"), this.mCxt.getVertexBufferObjectManager()); player = new Sprite(100, 100, getSpriteRegion(512, 512, "icon.png"), this.mCxt.getVertexBufferObjectManager()) { @Override protected void onManagedUpdate(float pSecondsElapsed) { // TODO Auto-generated method stub super.onManagedUpdate(pSecondsElapsed); player.setX(player.getX()+10); } }; attachChilds(); setBackground(bg); } private void attachChilds() { bg.attachParallaxEntity(new ParallaxEntity(0.0f, background_1, 640, 1136)); bg.attachParallaxEntity(new ParallaxEntity(0.0f, background_2, 640, 1136)); bg.attachParallaxEntity(new ParallaxEntity(0.0f, background_3, 640, 1136)); attachChild(player); this.mCxt.mCamera.setChaseEntity(player); } @Override protected void onManagedUpdate(float pSecondsElapsed) { // TODO Auto-generated method stub super.onManagedUpdate(pSecondsElapsed); this.mCxt.mCamera.updateChaseEntity(); } private TextureRegion getSpriteRegion(int width, int height, String name) { BitmapTextureAtlas atlas = new BitmapTextureAtlas(this.mCxt.getTextureManager(), width, height, TextureOptions.BILINEAR_PREMULTIPLYALPHA); atlas.load(); return BitmapTextureAtlasTextureRegionFactory.createFromAsset(atlas, this.mCxt.getAssets(), name , 0 , 0); } } 
