public class ParallelExecutor implements Runnable { // stores executions steps to private ConcurrentLinkedQueue<ExecutionStep> queue = new ConcurrentLinkedQueue<ExecutionStep>(); private ExecutorService threadPoolExecutor = Executors.newFixedThreadPool(10); private Thread feedingThread = new Thread(this); public void execute() { ExecutionStep step = new ConcreteExecutionStep(); this.queue.add(step); this.feedingThread.start(); } public void stop() { if (log.isInfoEnabled()) { log.info("Shutting down"); } this.queue = new ConcurrentLinkedQueue<ExecutionStep>(); try { this.feedingThread.join(0); this.threadPoolExecutor.shutdownNow(); } catch (InterruptedException e) { log.warn("Something happened!", e); } } public void run() { while (true) { if (this.threadPoolExecutor.isShutdown()) { return; } try { Thread.sleep(this.waitMillis); } catch (InterruptedException e) { // NOP } if (!this.queue.isEmpty()) { this.threadPoolExecutor.execute(new Runnable() { @Override public void run() { ExecutionStep step = this.queue.poll(); List<ExecutionStep> nextSteps = Collections.emptyList(); try { nextSteps = step.execute(); } catch (Exception e) { // NOP } // here we feed the queue this.queue.addAll(nextSteps); } }); } } } 
